import * as d3 from 'd3';
import * as $ from "jquery";
// import {interpolateYlGn} from "https://cdn.skypack.dev/d3-scale-chromatic@3";
d3.selection.prototype.moveToFront = function() {
    return this.each(function(){
      this.parentNode.appendChild(this);
    });
  };
function getUnique(value, index, self) {
    return self.indexOf(value) === index;
  }

function classExists(className){
    return document.getElementsByClassName(
        `${className}`
       ).length > 0;
}
let array = [];
export function launch(data, config){

    let getDimensions = ()=>{
        config.width = d3.select(`#${config.rootId}`).node().clientWidth,
        config.innerWidth = d3.select(`#${config.rootId}`).node().clientWidth - config.padding.left - config.padding.right,
        config.height = d3.select(`#${config.rootId}`).node().clientHeight,
        config.innerHeight = d3.select(`#${config.rootId}`).node().clientHeight - config.padding.top - config.padding.bottom;
    }
    let getDoses = ()=>{
        return data.lfc.map(d=>d.pert_dose).filter(getUnique)
    }

    getDimensions();
    let doses = getDoses();
    let dimensions = doses.filter(e=>e == doses[0] || e == doses[doses.length - 1]);

    let maxY = data.curve[0].upper_limit;
    //let minY = data.curve[0].lower_limit;

    /** adds a key for min and max dose. used for filtering data by max/min doses */
    data.curve.forEach(d=>{
        d[`${d.min_dose}`] =  d.points[0][1]
        d[`${d.max_dose}`] =  d.points[d.points.length -1][1]
    })

    let plot = d3.select(`#${config.rootId}`).append("g")
        .attr("transform",  `translate(${config.padding.left}, ${config.padding.right})`)
        .attr("id", "plot")

    config.scale = {
        x: d3.scaleLog().domain(d3.extent(doses)).range([0, config.innerWidth]),
        y: d3.scaleLinear().domain([0, maxY]).range([config.innerHeight, 0])
    }



    render(data.curve, config)
    table(data.curve)

    const x = d3.axisBottom()
        .scale(config.scale.x)
        .tickValues(doses)
        .tickSizeInner(config.innerHeight);     

    const y = [
            d3.axisLeft()
                .scale(config.scale.y)
                .tickSize(-config.innerWidth),
            d3.axisRight()
                .scale(config.scale.y)
    ];

    // Set the gradient
    plot.append("linearGradient")
        .attr("id", "gradient")
        .attr("gradientUnits", "userSpaceOnUse")
        .attr("x1", 0)
        .attr("y1", config.scale.y(maxY))
        .attr("x2", 0)
        .attr("y2", config.scale.y(0))
        .selectAll("stop")
        .data([
            {offset: "0%", color:d3.interpolateSpectral(1)},
            {offset: "100%", color: d3.interpolateSpectral(0)}
        ])
        .enter().append("stop")
        .attr("offset", function(d) { return d.offset; })
        .attr("stop-color", function(d) { return d.color; });

    // append x axis with non-log tick format..find better way?
    plot.append("g").attr("class", "axis x-axis")
        .call(x)
        .selectAll("text")
        .attr("dy", 14)
        .html((d,i)=>{ return doses[i]})

    var yAxis = plot.selectAll(".dimension")
        .data(dimensions)
        .enter().append("g")
        .attr("class", "dimension")
        .attr("transform", d=> `translate(${config.scale.x(d)},0)`)

    yAxis.append("g")
        .attr("class", "axis")
        .each(function(d,i) { 
            d3.select(this).call(y[i]);          
        })

    let brushWidth = 10;
    let brush = d3.brushY()
        .extent([[-brushWidth, config.scale.y.range()[1]], [brushWidth, config.scale.y.range()[0]]])
        .on("end", brushed);

    yAxis
        .append("rect")
        .attr("class", "brush-track")
        .attr("x", -8)
        .attr("y", 0)
        .attr("width", 16)
        .attr("height", config.innerHeight)
        .attr("fill", "url(#gradient)")
   // Add a brush for each axis.
   yAxis
        .append("g")
        .attr("class", "brush")
        .each(function() { d3.select(this).call(brush); })
        .selectAll("rect")
        .attr("x", -8)
        .attr("width", 16);

    function brushed() {
        var actives = [];
        plot.selectAll(".brush")
            .filter(function() {
                return d3.brushSelection(this);
            })
            .each(function(d) {
                actives.push({
                    dimension: d,
                    extent: d3.brushSelection(this),
                });
            });
           let filtered = data.curve.filter(d=>{
            return actives.every(function(active) {
                return active.extent[0] <= config.scale.y(d[active.dimension]) && config.scale.y(d[active.dimension])  <= active.extent[1];
            })
           })
           render(filtered, config)
           renderSelections()
           table(filtered)
           tableEvents()
    }

}

function render(data, config){

    let path = d3.line()
            .x(function(d) { return config.scale.x(d[0]) })
            .y(function(d) { return config.scale.y(d[1]) })
            .curve(d3.curveBasis)

    let plot = d3.select("#plot");

    let paths = plot.selectAll(".curve")
        .data(data)
    paths.exit().remove()

    paths
        .enter()
        .append("g")
        .merge(paths)
        .attr("class", "curve")
        .each(function(d){
            d3.select(this).selectAll("path").remove();
            d3.select(this).append("path")
                .attr("class", "path")
                .attr("d", path(d.points))
                .attr("stroke", "url(#gradient)" )
                .attr("fill", "none")
            d3.select(this).selectAll("text").remove();
            d3.select(this).append("text")
                .attr("x", 0)
                .attr("y", config.scale.y(d.points[0][1]))
                .html(d.ccle_name )
                .attr("text-anchor", "end")
                .attr("class", "label")
        })
        .on("click", function(d, e){  
            updateSelections(e)
            renderSelections()
               /** to handle path classes without using the selections array */
            // d3.select(this).moveToFront().classed("active", d3.select(this).classed("active") ? false : true);
            // d3.selectAll(".active").classed("inactive", false)
            // d3.selectAll(".curve:not(.active)").classed("inactive", true)

            // if (!classExists('active')){
            //     d3.selectAll(".inactive").classed("inactive", false)
            // }
        })
        .on("mouseover", function(){
            d3.select(this).moveToFront().classed("inactive", false)
            d3.selectAll(".active").moveToFront()
        })
        .on("mouseleave", function(){
            let classes = d3.select(this).attr("class");

            if (classes != "curve active"){
                d3.select(this).classed("inactive", true)
            }

        })
}

function table(data){
  let dTable = $('#table').DataTable();
  dTable.clear().rows.add(data).draw();
}

function tableEvents(){
    let dTable = $('#table').DataTable();
    $('#table tbody')
    .on('click', 'tr', function () {
        $('tr').removeClass('click')
        $(this).addClass('click')
        let d = dTable.rows('.click').data().toArray()[0]

     console.log(d)
    })
    .on('mouseover', 'tr', function () {
        $(this).addClass('mouseover')
        let d = dTable.rows('.mouseover').data().toArray()[0]
        console.log(d)

    })
    .on('mouseleave', 'tr', function () {
        $('tr').removeClass('mouseover')
        d3.selectAll("circle.mouseover").classed("mouseover", false)
    })
}


export function updateSelections(element){
    if (element.length == 0){
        array = []
    } else {
        if (array.indexOf(element) != -1) {
            array.splice(array.indexOf(element), 1)
        } else {
            array.push(element);
        }
    }
}

export function renderSelections(){

    d3.selectAll(".active").classed("active", false)

    let map = array.map(d=>d.ccle_name)

    d3.selectAll(".curve").filter(d=> {
        return map.includes(d.ccle_name)
    })
    .moveToFront()
    .classed("active", true)

    d3.selectAll(".active").classed("inactive", false)
    d3.selectAll(".curve:not(.active)").classed("inactive", true)

    if (!classExists('active')){
        d3.selectAll(".inactive").classed("inactive", false)
    }
}
