import * as d3 from 'd3';
import * as points from './models/points.js';
import * as helpers from './utils/helpers.js';
import * as scatterplot from './plots/scatterplot.js';

let doses; // this should be grabbed from somewhere across all sections?

// https://observablehq.com/@d3/working-with-color?collection=@d3/d3-color

function createVolcanoConfig(data, config, plotConfig){

    plotConfig = {
        rootId: config.rootId,
        // width: d3.select(`#${config.rootId}`).node().clientWidth,
        // height:  d3.select(`#${config.rootId}`).node().clientWidth,
        width: d3.select(`#${config.rootId}`).node().clientHeight,
        height:  d3.select(`#${config.rootId}`).node().clientHeight,
        padding: config.padding
    }; 

    // let cDomain = (Object.values(data).map(d=>d.c)).filter(helpers.getUnique).sort((a, b) => (Number.isFinite(a) ? a: Infinity) - (Number.isFinite(b) ? b : Infinity));
    let cDomain = doses;
    let cRange = d3.schemeSpectral[cDomain.length].reverse()
    // let cRange = d3.quantize(d3.interpolateHcl("#60c96e", "#4d4193"), cDomain.length)
    // let cRange = d3.quantize((d3.interpolateRgb.gamma(2.2)("red", "blue")), cDomain.length)

    /**
     * TO DO: add domains, ranges, labels to config so that render function is reusable
     */
    // let axis = ()=>{
    //     return {

    //     }
    // }
    let scale = ()=>{
        return { 
             x: d3.scaleLinear().domain(d3.extent(Object.values(data).map(e=>e.x))).range([0, plotConfig.innerWidth]).nice(),
             y: d3.scaleLinear().domain(d3.extent(Object.values(data).map(d=>d.y))).range([0, plotConfig.innerHeight]).nice(),
             c: d3.scaleOrdinal().domain(cDomain).range(cRange)
         }
    }
    plotConfig.innerWidth = plotConfig.width - config.padding.left - config.padding.right,
    plotConfig.innerHeight = plotConfig.height - config.padding.top - config.padding.bottom,
    plotConfig.scale = scale();
    return plotConfig
}


export function launch(data, config){

    doses = Object.values(data.tableData).map(d=>{
        if (Number.isFinite(+d.pert_dose)){ return +d.pert_dose }
        else { return d.pert_dose }
    }).filter(helpers.getUnique).filter(d=>d!=undefined).sort((a, b) => (Number.isFinite(a) ? a: Infinity) - (Number.isFinite(b) ? b : Infinity));

    let volcanoData = points.createScatterPoints(data.volcanoData)
    let volcanoConfig = createVolcanoConfig(volcanoData, config)
     scatterplot.render(volcanoData, volcanoConfig)

}
