    //             .attr("r", d=>d.r)
    //             .attr("fill", d=>config.scale.c(d.c))
    //             .attr("stroke", d=>config.scale.c(d.c))
    //             .attr("stroke-width", 2)
    //             .attr("fill-opacity", .5)
    //     })


        let circles = plot.selectAll("circle").data(data)
        circles.exit().remove()
        circles
            .enter().append("circle")
            .merge(circles)
            .attr("cx", d=> config.scale.x(d.x))
            .attr("cy", d=> config.scale.y(d.y))
            .attr("r", d=>d.r)
            .attr("fill", d=>config.scale.c(d.c))
            .attr("stroke", d=>config.scale.c(d.c))
            .attr("stroke-width", 2)
            .attr("fill-opacity", .5)

        var x = d3.axisBottom()
            .scale(config.scale.x)     

        plot.append("g").attr("class", "axis axis-x")
            .attr("transform", `translate(0,${config.innerHeight})`)
            .call(x)
        var y = d3.axisLeft()
            .scale(config.scale.y)     

        plot.append("g").attr("class", "axis axis-x")
            .attr("transform", `translate(0,0)`)
            .call(y)

}
